import React, {Component} from 'react';
import * as d3 from "d3";

const thickness = require('./data.json');
console.log("begin renderingindex");
class BarChart extends Component {
  componentDidMount() {
    this.drawChart();
  }

  drawChart() {
    //const data = [12, 50, 66, 86, 90, 208];
    const yplots = thickness['data']['plot_y'];
    const w = 200;
    const h = 400;
    const svg = d3.select("body")
    .append("svg")
    .attr("width", w)
    .attr("height", h)
    .style("margin-left", 100);
    /*
    svg.selectAll("rect")
      .data(data)
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 70)
      .attr("y", (d, i) => h - 10 * d)
      .attr("width", 65)
      .attr("height", (d, i) => d * 10)
      .attr("fill", "green")
    */
    console.log("begin rendering");
    svg.selectAll("circle")
      .data(thickness)
      .enter()
      .append("circle")
      .attr("cx", (thickness, i) => {var temp = thickness['data']['plot_x'][i];console.log('temp='+temp);return temp;})
      .attr("cy", (thickness, i) => thickness['data']['plot_y'][i])
      .attr("r", 100)
      //.attr("height", (d, i) => d * 10)
      .attr("fill", "green")
  }

  render(){
    return <div id={"#" + this.props.id}></div>
  }
}

export default BarChart;
