import React, {Component} from 'react';
import * as d3 from "d3";

const wall_data = require('./data.json');
class BarChart extends Component {
  componentDidMount() {
    this.drawChart();
  }

  drawChart() {
    //const data = [12, 50, 66, 86, 90, 208];
    const xplots = wall_data['data']['plot_x'];
    const yplots = wall_data['data']['plot_y'];
    const thickness = wall_data['data']['thickness'];
    var data = [];
    for (var i = 0 ; i < xplots.length; i++) {
      data.push({x: xplots[i], y: yplots[i], t: thickness[i] });
    }
    console.log('data size is ' + data.length);
    console.log('1500 is x:' + data[1500].x);
    const w = 800;
    const h = 800;
    const svg = d3.select("body")
    .append("svg")
    .attr("width", w)
    .attr("height", h)
    .style("margin-left", 100);
    /*
    svg.selectAll("rect")
      .data(data)
      .enter()
      .append("rect")
      .attr("x", (d, i) => i * 70)
      .attr("y", (d, i) => h - 10 * d)
      .attr("width", 65)
      .attr("height", (d, i) => d * 10)
      .attr("fill", "green")
    */
    svg.selectAll("circle")
      .data(data)
      .enter()
      .append("circle")
      .attr("cx", (d, i) => d.x)
      .attr("cy", (d, i) => d.y)
      .attr("r", 3)
      //.attr("height", (d, i) => d * 10)
      .attr("fill", "green")
  }

  render(){
    return <div id={"#" + this.props.id}></div>
  }
}

export default BarChart;
